using API.DTOs;
using API.DTOs.Settings;
using API.Module;
using Common;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Options;
using Microsoft.IdentityModel.Tokens;
using System.ComponentModel.DataAnnotations;
using System.IdentityModel.Tokens.Jwt;
using System.Runtime.ConstrainedExecution;
using System.Security.Claims;
using System.Text;

namespace API.Controllers
{
    public class SecurityController : Controller
    {
        private readonly SchoolManagementSystemContext _context;
        private readonly JwtSettings _jwtSettings;
        private readonly string _imageAvatar = "data:image/jpeg;base64,";

        public SecurityController(SchoolManagementSystemContext context, IOptions<JwtSettings> jwt)
        {
            _context = context;
            _jwtSettings = jwt.Value;
        }

        [AllowAnonymous]
        [HttpPost("register")]
        public async Task<IActionResult> Register([FromBody] UserPostDto user)
        {
            try
            {
                if (await _context.Users.AnyAsync(u => u.Email == user.Email))
                {
                    return BadRequest("User already exists.");
                }

                // please eneter the password 
                if (string.IsNullOrEmpty(user.Password))
                {
                    return BadRequest("Password is required.");
                }

                // should be complex password
                if (user.Password.Length < 8)
                {
                    return BadRequest("Password should be at least 8 characters long.");
                }

                // check the email and the username not exist before and not null
                if (string.IsNullOrEmpty(user.Email) || string.IsNullOrEmpty(user.UserName))
                {
                    return BadRequest("Email and UserName are required.");
                }

                // check the email is valid
                if (!new EmailAddressAttribute().IsValid(user.Email))
                {
                    return BadRequest("Email is not valid.");
                }

                // not exist before make sure not status!=9 deleted
                if (await _context.Users.AnyAsync(u => u.UserName == user.UserName && u.Status != 9))
                {
                    return BadRequest("UserName already exists.");
                }

                // email
                if (await _context.Users.AnyAsync(u => u.Email == user.Email && u.Status != 9))
                {
                    return BadRequest("Email already exists.");
                }

                User userObj = new User()
                {
                    BirthDate = user.BirthDate,
                    CreatedBy = null,
                    CreatedOn = DateTime.Now,
                    Email = user.Email,
                    FullName = user.FullName,
                    Password = Security.ComputeHash(user.Password, HashAlgorithms.SHA512, null),
                    PhoneNumber = user.PhoneNumber,
                    UserName = user.UserName,
                Photo = Convert.FromBase64String(_imageAvatar.Substring(_imageAvatar.IndexOf(",") + 1)),
                    Status = 0,
                    LoginTryAttempts = 0
                };

                _context.Users.Add(userObj);
                await _context.SaveChangesAsync();

                return Ok("User registered successfully.");

            }catch(Exception ex)
            {
                return BadRequest(ex.Message);
            }
        }

        [AllowAnonymous]
        [HttpPost("login")]
        public async Task<IActionResult> Login([FromBody] UserLoginDto userLoginDto)
        {
            var user = await _context.Users
                .SingleOrDefaultAsync(u => u.Email == userLoginDto.Email);

            if (user == null)
            {
                return Unauthorized("User not exist !!");
            }

            if (user.Status == 0)
            {
                return BadRequest("حسابك غير مفعل بعد الرجاء التواصل مع الادم ليتم قبولك !!");
            }

            if (user.Status == 2)
            {
                if (user.LoginTryAttemptDate != null)
                {
                    DateTime dt = (DateTime)user.LoginTryAttemptDate;
                    double minuts = 30;
                    dt = dt.AddMinutes(minuts);
                    if (dt >= DateTime.Now)
                    {
                        return BadRequest("لايمكنك الدخول للنظام: تم ايقافك");
                    }
                    else
                    {
                        user.Status = 1;
                        user.LoginTryAttempts = 0;
                        user.LoginTryAttemptDate = null;
                        _context.SaveChanges();
                    }
                }
                else { return BadRequest("لايمكنك الدخول للنظام: تم ايقافك"); }
            }

            if (!Security.VerifyHash(userLoginDto.Password, user.Password, HashAlgorithms.SHA512))
            {
                user.LoginTryAttempts = 
                    (short)(user.LoginTryAttempts == null ? 1 : (user.LoginTryAttempts + 1));
                if (user.LoginTryAttempts >= 5 && user.Status == 1)
                {
                    user.LoginTryAttemptDate = DateTime.Now;
                    user.Status = 2;
                }
                _context.SaveChanges();
                return NotFound("الرجاء التاكد من البريد الالكتروني وكلمة المرور");
            }

            user.LoginTryAttempts = 0;
            user.LoginTryAttemptDate = null;
            user.LastLoginOn = DateTime.Now;
            _context.SaveChanges();

            var token = GenerateJwtToken(user);

            UserDto userDto = new UserDto()
            {
                UserId = user.UserId,
                UserName = user.UserName,
                Email = user.Email,
                FullName = user.FullName,
                PhoneNumber = user.PhoneNumber,
                BirthDate = user.BirthDate,
            };

            return Ok(new { Token = token , user = userDto });
        }

        // Get Current User
        [HttpGet("GetCurrentUserInfo")]
        public async Task<IActionResult> Current()
        {
            var userId = User.FindFirstValue(ClaimTypes.NameIdentifier);
            var user = await _context.Users.FindAsync(int.Parse(userId));

            UserDto userDto = new UserDto()
            {
                UserId = user.UserId,
                UserName = user.UserName,
                Email = user.Email,
                FullName = user.FullName,
                PhoneNumber = user.PhoneNumber,
                BirthDate = user.BirthDate,
            };

            return Ok(userDto);
        }

        private string GenerateJwtToken(User user)
        {
            var tokenHandler = new JwtSecurityTokenHandler();
            var key = Encoding.ASCII.GetBytes(_jwtSettings.Secret);

            var tokenDescriptor = new SecurityTokenDescriptor
            {
                Subject = new ClaimsIdentity(new Claim[]
                {
                    new Claim(ClaimTypes.NameIdentifier, user.UserId.ToString()),
                    new Claim(ClaimTypes.Email, user.Email),
                    new Claim(ClaimTypes.Name, user.UserName),
                    new Claim(ClaimTypes.Role, "User"),
                    new Claim(ClaimTypes.NameIdentifier, user.UserId.ToString()),
                }),
                Expires = DateTime.UtcNow.AddMinutes(_jwtSettings.ExpiresInMinutes),
                Issuer = _jwtSettings.Issuer,
                Audience = _jwtSettings.Audience,
                SigningCredentials = new SigningCredentials(new SymmetricSecurityKey(key), SecurityAlgorithms.HmacSha256Signature)
            };

            var token = tokenHandler.CreateToken(tokenDescriptor);
            return tokenHandler.WriteToken(token);
        }

    }
}
